import random 
import numpy as np
from matplotlib import pyplot as plt


def question():
    a = random.randint(-10,10)
    b = random.randint(-10,10)

    if(a + b >= 0 and a * b >= 0): print("x^2 + " + str(a + b) + "x + " + str(a * b))
    if(a + b >= 0 and a * b < 0): print("x^2 + " + str(a + b) + "x - " + str(abs(a * b)))
    if(a + b < 0 and a * b >= 0): print("x^2 - " + str(abs(a + b)) + "x + " + str(a * b))
    if(a + b < 0 and a * b < 0): print("x^2 - " + str(abs(a + b)) + "x - " + str(abs(a * b)))

    x = np.arange(-12,12)
    y = (x + a)*(x + b)

    plt.title("Graph")
    plt.xlabel("X")
    plt.ylabel("Y")
    plt.ion()
    plt.plot(x, y, color="red")
    plt.show()
    print(a, b)
    intercepts = input("How many x-intercepts does this quadratic function have?\n")
    if(intercepts == "2"):
        print("Great! What are the intercepts? (Input one at a time)")
        ans1 = -1 * input()
        ans2 = -1 * input()
        if((ans1 == a or b) and (ans2 == a or b)): print("Great job! You got the x-intercepts")
        else: print("Sorry, that was not the correct answer.")
    else: print("Sorry that was incorrect!")
    
print("Welcome to mathabra!")
ready = input("Are you ready to start? (y/n)\n")
while(ready != "y"):
    ready = input("Okay, enter 'y' when you are ready\n")
question()






    
    

    

    
